mapper.update:
update PROGRAM
PG_NAME = #{PG_NAME}, PG_CATEGORY = #{PG_CATEGORY}, PG_TEACHER = #{PG_TEACHER}, PG_DAYSOFWEEK = #{PG_DAYSOFWEEK}, PG_START = to_date(#{PG_START}, 'YYYY-MM-DD"T"HH24:MI'), PG_END = to_date(#{PG_END}, 'YYYY-MM-DD"T"HH24:MI'), PG_CONTENT = #{PG_CONTENT}, PG_REPEAT_TYPE = #{PG_REPEAT_TYPE}, E_NAME = #{E_NAME}, REG_DATE = to_date(#{REG_DATE}, 'YYYY-MM-DD"T"HH24:MI'), MOD_DATE=sysdate, REG_ID = #{REG_ID}, MOD_ID = #{MOD_ID},
where PG_NO = #{PG_NO}
